import React, { Component } from 'react';
import Navbar from './componentes/navbar'
import 'bootstrap/dist/css/bootstrap.min.css'
//import datospersonas from '../src/datosPersonas.json'
import People from './componentes/people'

class App extends Component {

  state = {
    datospersonas: []
  }
  consultarApi = (state) => {
    const url = `https://uinames.com/api/?amount=25&ext`;
    console.log(url);
    fetch(url)
      .then(respuesta => respuesta.json())
      .then(respuesta => this.setState({ datospersonas }));
  };

  render() {
    return (
      <div className="container">
        <Navbar numeroTargetas={this.state.datospersonas.length} />
        <div className="card-group">
          <People datos={this.state.datospersonas} />
        </div>
      </div>
    );
  }
}


export default App;
